GtkWidget*
create_help (void)
{
  GtkWidget *help;
  GtkWidget *dialog_vbox1;
  GtkWidget *vbox6;
  GtkWidget *scrolledwindow2;
  GtkWidget *textview2;
  GtkWidget *dialog_action_area1;
  GtkWidget *close_help;

  help = gtk_dialog_new ();
  gtk_widget_set_size_request (help, 500, 400);
  gtk_window_set_title (GTK_WINDOW (help), _("Help"));
  gtk_window_set_type_hint (GTK_WINDOW (help), GDK_WINDOW_TYPE_HINT_DIALOG);

  dialog_vbox1 = GTK_DIALOG (help)->vbox;
  gtk_widget_show (dialog_vbox1);

  vbox6 = gtk_vbox_new (FALSE, 0);
  gtk_widget_show (vbox6);
  gtk_box_pack_start (GTK_BOX (dialog_vbox1), vbox6, TRUE, TRUE, 0);

  scrolledwindow2 = gtk_scrolled_window_new (NULL, NULL);
  gtk_widget_show (scrolledwindow2);
  gtk_box_pack_start (GTK_BOX (vbox6), scrolledwindow2, TRUE, TRUE, 0);
  gtk_scrolled_window_set_policy (GTK_SCROLLED_WINDOW (scrolledwindow2), GTK_POLICY_AUTOMATIC, GTK_POLICY_AUTOMATIC);
  gtk_scrolled_window_set_shadow_type (GTK_SCROLLED_WINDOW (scrolledwindow2), GTK_SHADOW_IN);

  textview2 = gtk_text_view_new ();
  gtk_widget_show (textview2);
  gtk_container_add (GTK_CONTAINER (scrolledwindow2), textview2);
  gtk_text_view_set_editable (GTK_TEXT_VIEW (textview2), FALSE);
  gtk_text_view_set_wrap_mode (GTK_TEXT_VIEW (textview2), GTK_WRAP_WORD);
  gtk_text_view_set_cursor_visible (GTK_TEXT_VIEW (textview2), FALSE);
  gtk_text_buffer_set_text (gtk_text_view_get_buffer (GTK_TEXT_VIEW (textview2)), _("The UCT IP Multimedia Subsystem Client Help\n----------------------------------------------------------\n\nPublic User Identity:\n\nThis is used to route the SIP signalling. \nUsage: <scheme>:<username>@<operator>\ne.g. sip:alice@open-ims.test\n\nPrivate User Identity:\n\nExclusively used for subscription identification and authentication purposes.\nUsage: <username@operator>\ne.g. alice@open-ims.test\n\nProxy CSCF:\n\nFirst point of contact between the terminal and the IMS network.\nUsage: <pcscf url>[:port]\ne.g. pcscf.open-ims.test:4060\n\nRealm:\n\nName of network.\nUsage: <network url>\ne.g. open-ims.test\n\nPassword:\n\nMust correspond to the password stored in the HSS.\n\nQoS:\n\nDesired level of QoS. If no QoS signalling is desired (e.g. 183 session progress, UPDATE, etc.) then set this to none. Refer to RFC 3312.\n\nQoS Type:\n\nType of QoS required. Refer to RFC 3312.\n\nAccess Network:\n\nType of access network. Refer to RFC 3455.\n\nAssociated URIs:\n\nA list of URIs that the network operator has reserved for the user.\n\nReg Event:\n\nInformation about the current registrations. Refer to RFC 3680.\n\nFor more information and how-to guides please visit the website at \n\n  http://uctimsclient.berlios.de"), -1);

  dialog_action_area1 = GTK_DIALOG (help)->action_area;
  gtk_widget_show (dialog_action_area1);
  gtk_button_box_set_layout (GTK_BUTTON_BOX (dialog_action_area1), GTK_BUTTONBOX_END);

  close_help = gtk_button_new_from_stock ("gtk-close");
  gtk_widget_show (close_help);
  gtk_dialog_add_action_widget (GTK_DIALOG (help), close_help, GTK_RESPONSE_CLOSE);
  GTK_WIDGET_SET_FLAGS (close_help, GTK_CAN_DEFAULT);

  g_signal_connect ((gpointer) close_help, "clicked",
                    G_CALLBACK (on_close_help_clicked),
                    NULL);

  /* Store pointers to all widgets, for use by lookup_widget(). */
  GLADE_HOOKUP_OBJECT_NO_REF (help, help, "help");
  GLADE_HOOKUP_OBJECT_NO_REF (help, dialog_vbox1, "dialog_vbox1");
  GLADE_HOOKUP_OBJECT (help, vbox6, "vbox6");
  GLADE_HOOKUP_OBJECT (help, scrolledwindow2, "scrolledwindow2");
  GLADE_HOOKUP_OBJECT (help, textview2, "textview2");
  GLADE_HOOKUP_OBJECT_NO_REF (help, dialog_action_area1, "dialog_action_area1");
  GLADE_HOOKUP_OBJECT (help, close_help, "close_help");

  return help;
}

